    /**
     * 重新计算成就积分
     */
    @Test
    public void recalAchievementScore() {
        List<CfgAchievementEntity> allAchievements = AchievementTool.getAllAchievements();
        Map<Integer, Integer> typeScoreMap = allAchievements.stream().collect(Collectors.groupingBy(CfgAchievementEntity::getType, Collectors.summingInt(CfgAchievementEntity::getScore)));
        int sumScore = 0;
        for (Integer type : typeScoreMap.keySet()) {
            System.out.println(AchievementTypeEnum.fromValue(type).getName() + ":" + typeScoreMap.get(type));
            sumScore += typeScoreMap.get(type);
        }
        System.out.println("总积分：" + sumScore);
    }

    @Test
    public void testEffective() {
        for (int i = 0; i < 100; i++) {
            System.out.println(TypeEnum.getRandomNotCounterattackType(50));
        }
        long start = System.currentTimeMillis();
        List<CfgCardEntity> cards = CardTool.getAllCards();
        System.out.println(System.currentTimeMillis() - start);
        start = System.currentTimeMillis();
        cards = CardTool.getAllCards();
        System.out.println(System.currentTimeMillis() - start);
        start = System.currentTimeMillis();
        cards = CardTool.getAllCards();
        System.out.println(System.currentTimeMillis() - start);
        start = System.currentTimeMillis();
        cards = CardTool.getAllCards();
        System.out.println(System.currentTimeMillis() - start);
    }

