    @Test
    public void ymlToSql() {
        List<CfgCardEntity> cards = CardTool.getAllCards();
        cards.forEach(tmp -> {
            String sql = "INSERT INTO `god_game`.`cfg_card`(`id`, `name`, `type`, `star`, `attack`, `hp`, `zero_skill`, `five_skill`, `ten_skill`, `comment`, `group`, `can_get_way`) VALUES (";
            sql += tmp.getId() + ",'" + tmp.getName() + "'," + tmp.getType() + "," + tmp.getStar() + "," + tmp.getAttack() + "," + tmp.getHp() + ","
                    + (tmp.getZeroSkill() == 0 ? "NULL" : tmp.getZeroSkill()) + ","
                    + (tmp.getFiveSkill() == 0 ? "NULL" : tmp.getFiveSkill()) + ","
                    + (tmp.getTenSkill() == 0 ? "NULL" : tmp.getTenSkill()) + ","
                    + (tmp.getComment() == null ? "NULL" : "'" + tmp.getComment() + "'") + ","
                    + (tmp.getGroup() == 0 ? "NULL" : tmp.getGroup()) + ","
                    + (tmp.getWay() == null ? "NULL" : tmp.getWay()) + ");";
            System.out.println(sql);

        });
    }

